INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (52, 'Броневик', 'Armored Car', '', 2, 2, 4, 'В бокале с несколькими кубиками льда смешать равные части джина, текилы и амаретто.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (55, 'А Ц Лайм', 'AC Lime', '', 2, 4, 4, 'В старомодный бокал со льдом налить лимонную водку и кофейный ликер.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (181, 'Амнезия', 'Amnesia', '', 2, 2, 4, 'В широкий стакан налить крепкий ром (Bacardi 151) добавить амретто. Перемешать и дополнить Спрайтом.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (59, 'Абердин-Ангус', 'Aberdeen Angus', '', 2, 2, 4, 'В кружку налить шотландский виски, сок лайма и мёд. Лиекр Драмбуи нагреть на плите и поджечь его. Дать немного погореть и перелить в кружку к остальным ингредиентам. Размешать и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (81, 'Аггравация', 'Aggrаvation', '', 2, 2, 4, 'В бокал со льдом налить две части шотландского виски и по одной части кофейного ликера и сливок.', null, 0, null, false, false, false, 60, 'highball');
