INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (70, 'Буллводка', 'Bullvodka', '', 2, 2, 4, 'В стакане смешать водку и энергетический напиток, добавить пару кубиков льда, соломинку и подавать.', null, 0, null, false, false, false, 20, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (78, 'Бум Джус', 'Bum Juice', '', 2, 2, 4, 'Из двух полуторалитровых бутылок любой газировки (обычно апельсиновой или лимонной) отлить по 375 мл, а вместо них влить по полбутылки дешёвого рома. Закрыть крышкой. слегка перемешать и осторожно открыть.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (102, 'В Бубен', 'At the Tambourine', '', 2, 4, 4, 'На пьющего надевается строительная, или какая есть, каска. Отвлекается его внимание и накрыв рукой стакан бьют им по каске.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (115, 'Акапулько', 'Acapulco', '', 2, 2, 4, 'В шейкере со льдом смешать ром, Трипл Сек, Сок лайма, сахар и белок от яйца, перелить в стакан и украсить листочками мяты.', null, 0, null, false, false, false, 30, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (129, 'Алая Корона', 'Bloody Corona', '', 2, 2, 4, 'Открыть хорошо охлажденную бутылку светлого мексиканского пива, плеснуть в нее гренадина, чуть перемешать ротациоными движениями, поднести к губам и наслаждаться.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (136, 'Ведро', 'Bucket', '', 2, 4, 4, 'В ведро налить две части спирта, три части водки и четыре части апельсинового сока. Лёд по вкусу.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (138, 'Ведро Джина', 'Gin Bucket', '', 2, 4, 4, 'Наложить половину ведра льда, налить в него 1,75 литра джина, две двухлитровых бутылки Спрайта или 7Up, еще, добавить пол литра сока лайма (он продается в бутылочках, в магазине) и добавить дольки свежего лайма или лимона.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (166, 'Взрыватель Оков', 'Ballchain Blaster', '', 2, 2, 4, 'В стакане со льдом смешать ирландский виски (Jameson) и Доктор Пеппер. Подавать!', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (149, 'Альфредо', 'Alfredo', '', 2, 2, 4, 'В шейкере со льдом встряхнуть Кампари и джин. Процедитьв бокал и подавть.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (150, 'Верно', 'Verno', '', 2, 2, 4, 'В стакан налить одну часть рома и три пива.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (151, 'Альянс', 'Alliance', '', 2, 4, 4, 'Шейкер до половины наполнить колотым льдом, добавить джин, сухой вермут и плеснуть Аквавит. Встряхнуть, перелить в старомодный бокал со льдом и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (173, 'Амаретто Сауэр', 'Amaretto Sour', '', 2, 2, 4, 'Края старомодного бокала смочить соком лимона и обвалять в сахарном песке.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (175, 'Гранатовый Американо', 'Pomegranate Americano', '', 2, 2, 4, 'В стакан со льдом налить гранатовый сок и добавить 5 дэшей (20 капель) биттера (горькой настойки) Angostura.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (178, 'Висилично-Бледное Пиво', 'Stifler&#039;s Pale Ale', '', 2, 2, 4, 'В бутылку пива налить рюмку айриш крема и начать быстро пить, пока айриш крем не растворился в пиве.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (180, 'Виски с Вишней', 'Whiskey and Cherry', '', 2, 2, 4, '', null, 0, null, false, false, false, 40, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (186, 'Виски с Колой', 'Whiskey Cola', '', 2, 2, 4, 'Можно подавать в таком виде, а можно еще отжать дольку лимона или лайма.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (188, 'Бостонский Сауэр', 'Boston Sour', '', 2, 4, 4, 'Все ингредиенты встряхнуть в шейкере со льдом. Перелить в стакан для виски с несколькими кубиками льда. Украсьте вишенкой, долькой лимона и подавайте.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (190, 'Vit Ryss', '', '', 2, 2, 4, 'В стакан с колотым льдом налить водку, кофейный ликер и дополнить молоком.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (234, 'Вопль Повстанцев', 'Rebel Yell', '', 2, 2, 4, 'Процедить в широкий бокал и подавать, украсив долькой апельсина.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (236, 'Воскресный Утренний  Съем', 'Sunday Morning Pick-up', '', 2, 4, 4, 'В старомодный бокал с несколькими кубиками льда налить по одной части текилы, Бехеровки и Ягермастера.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (239, 'Апероль с Апельсином', 'Aperol and Orange', '', 2, 2, 4, 'В стакан со льдом налить треть итальянского сладкого вермута (Aperol) и две части апельсинового сока.', null, 0, null, false, false, false, 20, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (240, 'Восхищение Мафиози', 'Mafioso Delight', '', 2, 2, 4, 'В стакан со льдом налить бренди, джин и анисовый ликер (Pernod, если нет, то можно заменить и абсентом.)', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (249, 'Аркхем Азилум', 'Arkham Asylum', '', 2, 2, 4, 'По мере таяния льда и перемешивания коктейля можно наблюдать образование облаков в бокале.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (260, 'Вуу Вуу', 'Woo Woo', '', 2, 2, 4, 'В стакан со льдом налить водку, персиковый шнапс и дополнить клюквенным соком.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (264, 'Гавайская Утренняя Роса', 'Hawaiian Morning Dew', '', 2, 2, 4, 'Бзять бутылку Маунтин Дью, отпить из нее от трети до половины и дополнить ананасовым соком и светлым ромом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (272, 'Газировка СССР ', 'Aerated Water USSR', '', 2, 2, 4, 'В граненый стакан по стеночке налить содовую до половины, и дополнить лимонадом Саяны. Хотя бы один из ингредиентов должен быть хорошо охлажденным.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (277, 'Африканская Королева', 'African Queen', '', 2, 2, 4, 'В кружку налить амаретто и трипл сек, дополнить горячим кофе и украсить взбитыми сливками.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (296, 'Генуя', 'Genoa', '', 2, 2, 4, 'В старомодный стакан со льдом налить граппу, самбуку и сухой вермут.', null, 0, null, false, false, false, 40, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (310, 'Глазное Яблоко', 'Apple Of My Eye', '', 2, 2, 4, 'Смешать яблочный сок и чайную ложку черносмородинового сиропа, перелить в бокал, украсить сверху взбитыми сливками и чуть присыпать корицей.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (312, 'Глинтвейн', 'Gluhwein', '', 2, 2, 4, 'В кастрюлю налить литр красного вина, добавить три столовых ложки мёда, 1-2 палочки корицы, 7 гвоздик, тертый мускатный орех - треть, нарезанный на кружки апельсин. Можно по желанию и яблока долькт добавить. Довести до температуры 70 градусов под крышкой. Дать постоять 10-15 минут и разливать по бокалам.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (321, 'Базука Джо', 'Bazooka Joe', '', 2, 2, 4, 'В стакан со льдом налить лимонный ром и дополнить энергетическим напитком.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (338, 'Головоломка', 'Brain Teaser', '', 2, 2, 4, 'В шейкере со льдом встряхнуть персиковый ликер, амаретто и сливки. Процедить в бокал с несколькими кубиками льда и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (347, 'Банановый Жук', 'Banana Beetle', '', 2, 2, 4, 'В стакан со льдом налить Егермейстер, банановый ликер и дополнить колой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (442, 'Грейпфрут и Апельсин', 'Grapefruit and Orange', '', 2, 2, 4, 'В бокал со льдом налить 1 часть апельсинового сока и 3 части грейпфрутового сока.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (349, 'Бандитос', 'Banditos', '', 2, 4, 4, 'Для приготовления и употребления требуется два человека.  Принимающий(ая) садится на подобранные под себя ноги на стул или на стоку, а подающий(ая) берет в левую руку бутылку текилы, в правую - бутылку кофейного ликера (лучше, чтобы на бутылки были надеты гейзеры (насадки на горлышко, позволяющие наливать напиток тонкой струйкой), а в губы дольку лайма.  Подающий(ая) встает лицом к принимающему(ей) и с двух сторон начинает наливать напитки в рот. Затем прямо из своих губ выжимает сок лайма (часто сопровождая данное действие поцелуем) после чего принимающий(ая) глотает коктейль.    Совет для тех, кто решил попробовать данный коктейль!  Принимающая и подающий, или принимающий и подающая (однополые варианты мы не рассматриваем) должны быть глубоко симпатичны друг другу, иначе ощущения от коктейля будут размыты, и не так приятны.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (368, 'Голубой Гавайский Винт ', 'Blue Hawaiian Screw', '', 2, 2, 4, 'В стакан наложить льда, влить водку и голубой кюрасао, затем дополнить стакан равными частями апельсинового и ананасового сока, украсить кружком из апельсина и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (381, 'Батерфингер', 'Butterfinger', '', 2, 2, 4, 'В стакан со льдом налить четверть айриш крема, четверть - ирисового шнапса, и дополнить молоком. Перемешать и подавтаь с соломинкой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (480, 'Грязная Птица', 'Dirty Bird', '', 2, 2, 4, 'В стакан со льдом налить водку и кофейный ликер и перемешать. Долить сливки, не перемешивая, и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (989, 'Кавказский', 'Caucasian', '', 2, 2, 4, 'В стакан со льдом налить водки, кофейного ликера, и по вкусу, нежирных сливок. Коктейль готов!', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (398, 'Гончий Пес', 'Hound Dog', '', 2, 2, 4, 'Будьте осторожны с перцем и помните, что его острота раскрывается не сразу.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (406, 'Горький Конец', 'Bitter End', '', 2, 2, 4, 'В сткан со льдом налить сухой вермут и плеснуть немного Ангостуры.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (407, 'Белое Зарево', 'White Glow', '', 2, 4, 4, 'Коктейль готов!
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (414, 'Горячая Ваниль', 'Hot Vanilla', '', 2, 2, 4, 'В кастрюле довести до кипения молоко, сахар и ваниль (лучше использовать стручковую, половинки стручка будет достаточно), периодически помешивая и снимая пленку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (422, 'Горячий Секс', 'Hot Sex', '', 2, 2, 4, 'Встакане со льдом смешать трипл сек и апельсиновый сок, плеснуть гренадина и, добавив коктейльную вишню, подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (423, 'Белый Монах', 'White Monk', '', 2, 2, 4, 'В шейкере со льдом встряхнуть до вспенивания водку, арахисовый ликер и сливки. Перелитьв бокал со льдом и подавть.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (424, 'Горячий Шоколад', 'Hot Chocolate', '', 2, 2, 4, 'В чашку положить какао порошок, залить горячим молоком, добавить сахар и чуть соли. Сверху добавить взбитые сливки и посыпать тертым шоколадом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (425, 'Белый Русский', 'White Russian', '', 2, 2, 4, 'В стакан со льдом налить водку, кофейный ликер и сливки. Перемешать и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (427, 'Белый Сибирский', 'White Siberian', '', 2, 2, 4, 'Перемешать и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (431, 'Бензиновый Милкшейк', 'Gasoline milkshake', '', 2, 2, 4, 'В шейкере со льдом встряхнуть 151 ром, амаретто и айриш крем, пока не вспенится.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (663, 'Алабамский Дожигатель', 'Alabama Afterburner', '', 2, 2, 4, 'В старомодный стакан налить темный ром, а затем имбирный эль и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (449, 'Бешеная Энергия', 'Mad Energy', '', 2, 4, 4, 'Рецепт от посетителя: Рецепт на 1л  Энергетик любой 0,5л,  Настойка Шуйская   250мл,  Коньяк (лучше армянский) 250мл.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (479, 'Блэкаут', 'Blackout', '', 2, 4, 4, 'В шейкере с колотым льдом встряхнуть джин, черничный ликёр, лимонный сок и сахарный сироп.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (481, 'БМВ', 'BMW', '', 2, 2, 4, 'В старомодный бокал налить айриш крем (<strong>B</strong>ailey''s Irish cream), кокосовый ром (<strong>M</strong>alibu coconut rum) и виски (<strong>W</strong>hisky) и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (461, 'Бог Биошока', 'Bioshock Infinite', '', 2, 2, 4, 'В шейкер поместить веточку базилика и по половинке лимона и лайма порезанного на дольки. Хорошенько размять ихь мадлером и влить долю сахарного сиропа. Влить две доли джина и дополнить льдом. Хорошенько втстряхнуть и процедить в бокал.', null, 0, null, false, false, false, 50, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (468, 'Грузинский Абуз', 'Georgian Watermelon', '', 2, 2, 4, 'В старомодный бокал со льдом налить водку, клубничный ликер, кисло-сладкий микс и гренадин. Перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (476, 'Грязная Мать', 'Dirty Mother', '', 2, 2, 4, 'В старомодный стакан со льдом налить бренди и кофейный ликер, хорошенько перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (477, 'Блэк Джек', 'Blackjack', '', 2, 4, 4, 'Смешать в стакане для виски равные доли виски и черной самбуки, добавить лед и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1562, 'Сепаратор', 'Separator', '', 2, 2, 4, 'В стакан со льдом налить равные части бренди, темного крем де какао ликера и сливок.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (484, 'Грязь Теннесси', 'Tennessee Mud', '', 2, 2, 4, 'В кофейную кружку налить виски, амаретто и дополнить горячим кофе. Украсить взбитыми сливками и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (506, 'Дамский угодник', 'Ladyfriend', '', 2, 4, 4, 'В старомодный бокал с несколькими кубиками льда влить Амаретто и грейпфрутовый сок.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (498, 'Золотистый Дайкири', 'Golden Daiquiri', '', 2, 2, 4, 'В стакан положить малину и размять ее с коричневым сахаром. В шейкере со льдом встряхнуть золотой ром и сок лайма. Процедить в стакан, добавить пару кубиков льда и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (503, 'Большой Куш', 'Snatch', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (512, 'Два', 'Two', '', 2, 2, 4, 'В стакан положить несколько кубиков льда и смешать в нем водку с вишневым и апельсиновым соком.', null, 0, null, false, false, false, 30, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (516, 'Двустволка', 'Double-Barrelled Gun', '', 2, 2, 4, 'Суть коктейля двустволка заключается в том, что портвейн и пиво пьется одновременно из двух бутылок.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (518, 'Дебошир', 'Brawler', '', 2, 2, 4, 'В пластиковую бутылку налить бутылку водки, банку сгущенного молока, добавить чайную ложку растворимого кофе, (воду по вкусу) и перемешивать до полного растворения кофе и сгущенки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (836, 'Красный Октябрь', 'Red October', '', 2, 4, 4, 'Коктейль можно украсить, добавив кружо лайма.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (527, 'Танец Кукушки и Крокодила', 'Cuckoo and Crocodile Dance', '', 2, 2, 4, 'В трёхлитровой бутылке смешать все ингредиенты. Пиво - любой марки. Вино берётся красное сладкое.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (557, 'Дезинтегратор', 'Disintegrator', '', 2, 4, 4, 'Для дезинтеграции понадобится маленькая бутылочка коньяка (любого) емкостью 200-250 мл и бутыка пива.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (576, 'Капучине', 'Capucine', '', 2, 2, 4, 'В стакан с колотым льдом налить мятный сироп и дополнить  нежирными сливками. присыпать немного тертым шоколадом и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (561, 'Делающий Вдовами', 'Widow Maker', '', 2, 2, 4, 'В стакан со льдом налить водку, ягермастер и кофейный ликер. Размешать и добавить пару капель гренадина.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (580, 'Карбонозавр', 'Cabronasaurus', '', 2, 2, 4, 'Взять 26 жареных кофейных зерен, молоко, мороженое и корчиный шнапс - взбить в течении 55 секунд в блендере, перелить в кружку и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (572, 'Капри', 'Capri', '', 2, 2, 4, 'В шейкере со льдом встряхнуть белый крем де какао, крем де бананес и сливки, перелить в бокал со льдом и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (593, 'Джеральд', 'Jerald', '', 2, 2, 4, 'Можно добавить пару кубиков льда.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (597, 'Джи Дринк', 'G - Drink', '', 2, 2, 4, 'В стакане смешать коньяк и яблочный сок. Добавить лёд, по вкусу и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (696, 'Коктейль Молотова', 'Molotov&#039;s cocktail', '', 2, 4, 4, 'В шейкере со льдом чуть встряхнуть водку и мятный шнапс, процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (585, 'Джек и Джилл', 'Jack and Jill', '', 2, 2, 4, 'В маленький пивной бокал со льдом налить бурбон (Jack Daniels) плеснуть гренадина и сока лайма, дополнить имбирным элем и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (594, 'Карск', 'Karsk', '', 2, 4, 4, 'В кофейную чашечку положить чистенькую монетку. Наливать кофе до того момента, пока монетки не будет видно. Затем наливать виски, до того момента, когда монету снова станет видно.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (605, 'Джин Бак', 'Gin Buck', '', 2, 2, 4, 'В старомодный стакан со льдом налить джин и сок половинки небольшого лимона.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (697, 'Дынная Лихорадка', 'Melon Fever', '', 2, 2, 4, 'Перемешать и подавать.', null, 0, null, false, false, false, 30, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (621, 'Джин Кассис', 'Gin Cassis', '', 2, 4, 4, 'Подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (626, 'Кенни', 'Kenny', '', 2, 2, 4, 'В стакана с парой кубиков льда положить дольку консервированного персика. Влить водку и дополнить Спрайтом.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (628, 'Кефир-Кола', 'Kefir-Kola', '', 2, 2, 4, 'Наполнить половину широкого бокала колой, а потом влить в него кефир. Подавать не перемешивая.    Кефир постепенно погружается и постепенно расслаивается. Пить его лучше до расслоения.  Отлично помогает с похмелья.', null, 0, null, false, true, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (632, 'Кингстонский скорпион', 'Kingston Scorpion', '', 2, 2, 4, 'В кувшине,заполненном льдом,смешать ингредиенты. Добавить несколько коктейльных вишен и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (633, 'Апельсиновый Джин Физ', 'Orange Gin Fizz', '', 2, 2, 4, 'В шейкере со льдом в течении трех минут интенсивно встряхивать джин, апельсиновый сок, дэш гренадина и яичный белок.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (645, 'Джуниор Минт', 'Junior Mint', '', 2, 2, 4, 'В шейкере или стакане для смешивания со льдом смешать кокосовый ром (Malibu Coconut Rum), какао ликер (white creme de cacao) и мятный ликер (white creme de menthe) процедить в бокал и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (651, 'Дикая Штучка', 'Wild Thing', '', 2, 2, 4, 'В стакан со льдом налить текилу, клюквенный сок, сок лайма и дополнить содовой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (661, 'Дождливая Среда', 'Rainy Wednesday', '', 2, 2, 4, 'В стакне для смешивания размять очищенный от кожуры мандарин с сахарной пудрой мадлером. Добавить лёд, влить бренди перемешать и процедить в бокал со льдом. дополнить содовой и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (842, 'Красный Русский', 'Red Russian', '', 2, 2, 4, 'В стакан со льдом налить вишневый ликер и водку.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (843, 'Зеленая Киска', 'Green Pussy', '', 2, 2, 4, 'В стакане смешать светлый ром, дынный ликер и кисло-сладкий микс.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (845, 'Зеленая Корова', 'Green Cow', '', 2, 2, 4, 'Смешать равные части зеленого бананового ликера (Pisang Ambon) и молока. Перелить в старомодный бокал и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (848, 'Кремат', 'Cremat', '', 2, 2, 4, 'Влейте все компоненты в терракотовый горшок. Отдельно подогрейте некоторое количество рома на печи. Как только ром нагреется, подожгите его и медленно лейте его в терракотовый горшок. Размешивайте напиток медленно, пока не остается 2/3 жидкости, затем разливайте по кружкам. Количество ингредиентов приведено для 8 человек.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (852, 'Крепкий Орешек', 'Die Hard', '', 2, 2, 4, 'В старомодный стакан со льдом налить спирт, водку и грейпфрутовый сок. Украсить апельсиновой коркой и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (854, 'Крёстная мать', 'Godmother', '', 2, 4, 4, 'В старомодный бокал, заполненный льдом, влить водку и ликёр Амаретто.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (858, 'Крёстный отец', 'Godfather', '', 2, 4, 4, 'В старомодном бокале с несколькими кубиками льда смешать шотландский виски и амаретто. Добавить апельсин, порезанный дольками, и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (863, 'Французский Зеленый Дракон', 'French Green Dragon', '', 2, 2, 4, 'В шейкере со льдом встряхнуть коньяк и зеленый дынный ликер (<span class="ingredient"><span class="name">Midori melon liqueu). Процедить в бокал и подавать.							 							 							</span></span>', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (872, 'Кровавая Резня', 'Blood Bath', '', 2, 2, 4, 'Процедить в бокал и подавать. Можно добавить пару кубиков льда.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (920, 'Крымский', 'Crimean', '', 2, 4, 4, 'В стакане для смешивания смешайте белое сухое вино (лучше всего, Крымское Алиготе), трипл сек и сок лимона.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (888, 'Кровосос', 'Blood Sucker', '', 2, 2, 4, 'На стенки стакана, с внутренней стороны капнуть несколько капель гренадина, наложить льда и процедить содержимое шейкера.', null, 0, null, false, false, false, 40, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (912, 'Крылатый капитан', 'Winged Captain', '', 2, 2, 4, 'В стакан со льдом налить ром (Captain Morgan Original spiced rum) и баночку энергетического напитка (Red Bull)', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (944, 'Кубинский Президент', 'Cuban El Presidente', '', 2, 2, 4, 'Перемешать и подавать.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (948, 'Кофейный Кузнечик', 'Coffee Grasshopper', '', 2, 4, 4, 'В шейкере смешать компоненты и перелить в старомодный бокал поверх нескольких кубиков льда.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (950, 'Кукан Боярского', 'Boyarskiy&#039;s Kukan', '', 2, 2, 4, 'Суровый коктейль современных российских неформалов.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (963, 'Испанский Шоколад', 'Spanish Chocolate', '', 2, 2, 4, 'В блендере взбить молоко и тертый шоколад с корицей до получения взвеси, добавить яичный желток и вбивать еще несколько секунд, когда коктейль вспенится - перелить его в кружку и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (966, 'Ла Мохита', 'La Mojita', '', 2, 2, 4, 'В стакан положить листья мяты, лайм и лёд и сахар. Раздавить их пестиком или ложкой. Долить водку содовую и положить малину.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (967, 'Итальянская Содовая', 'Italian Soda', '', 2, 2, 4, 'В бокал со льдом налить малиновый ликер и дополнить содовой. Не перемешивать. Подавать с соломинкой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1003, 'Кайманов Папа', 'Cayman Papa', '', 2, 2, 4, 'В шейкере со льдом встряхнуть золотой ром, нектар манго и апельсиновый сок. Перелить в стакан со льдом и на поверхность наслоить амаретто. Украсить и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1179, 'Мариачи', 'Mariachi', '', 2, 2, 4, 'В старомодном стакане смешать самбуку и кофейный ликер. Плеснуть соуса Табаско и подавать насадив на краешек стакана дольку перца чили или халапеньо.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1007, 'Кроваво-Апельсиновая Кайиринья', 'Blood Orange Caipirinha', '', 2, 2, 4, 'В шейкер поместить нарезанную на маленькие дольки половину красного апельсина и четвертинку лайма. Добавить сахарную пудру и размять все мадлером.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1009, 'Кайпириссима', 'Сaipirissima', '', 2, 2, 4, 'В старомодый бокал положить разрезанный на 4 части лайм и размять его пестиком. Добавить 2 ложечки коричневого сахара. Наложить в стакан льда и залить ромом.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1011, 'Кайпироска', 'Caipiroska', '', 2, 2, 4, 'В старомодный бокал положить 4 четвертинки лайма и размять пестиком. Положить 2 ложечки тростникового сахара и засыпать лёд.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1012, 'Лемон Кордиал', 'Lemon cordial', '', 2, 2, 4, 'Перелить в кастрюлю и довести до кипения.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1019, 'Калимохо', 'Kalimocho', '', 2, 2, 4, 'Для приготовления коктейля понадобится литровый тетрапак с холодным красным сухим вином и двухлитроавя бутылка холодной колы.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1032, 'Летучий Голландец', 'Flying Dutchman', '', 2, 2, 4, 'В стакан со льдом налить джин и трипл сек, хорошенько перемешать, украсить кружочком из лимона и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1087, 'Лимончелло Шприцер', 'Limocello Spritzer', '', 2, 2, 4, 'В стакн со льдом налить водки, отжать сока лимона и дополнить тоником. Украсить спиралькой из корки лимона и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1238, 'Привет Полиция', 'Hallo Police', '', 2, 2, 4, 'В шейкере со льдом встряхнуть равные части водки и лимонного сока, процедить в старомодный бокал и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1125, 'Мавигатор', 'Maviagator', '', 2, 2, 4, 'Встакан со льдом налить сначала пряный ром (Captain Morgan spiced rum) а потом айриш крим ( Bailey''s Irish cream) Подавать не перемешивая.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1139, 'Малена', 'Malena', '', 2, 2, 4, 'Рецепт от посетителя:
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1149, 'Малиновый Русский', 'Raspberry Russian', '', 2, 2, 4, 'В бокал со льдом налить равные части малиновой водки и кофейного ликера, дополнить молоком и подавать.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1151, 'Малыш Бехер', 'Baby Becher', '', 2, 2, 4, 'В старомодный бокал со льдом налить водку, Бехеровку и тоник. Украсить листочками мяты и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1156, 'Польский медовый с ревенём', 'Polish Rhubarb Honey Drink', '', 2, 2, 4, 'Подогретую воду смешать с мёдом (на 3-4 чашки воды - 1/2 чашки мёда).
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1161, 'Мандарин', 'Mandarin', '', 2, 2, 4, 'В стакане со льдом смешать мандариновый, вишневый и грушевый соки. Украсить и поадавать.
