INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1938, 'Эль Диабло', 'El Diablo', '', 2, 2, 4, 'Вбокале со льдом смешать текилу, черносмородиновый ликер (cr&egrave;me de cassis), добавить имбирный эль и отжать дольку лайма.', null, 0, null, false, false, false, 60, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1940, 'Эль Дорадо', 'El Dorado', '', 2, 2, 4, 'В шейкере со льдом встряхнуть мёд, текилу и сок лимона. Процедить в бокал с колотым льдом и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1945, 'Ублюдок Пустыни', 'Desert Bastard', '', 2, 2, 4, 'В старомодный бокал налить текилу, виски и дополнить яблочным соком. Компоненты использовать охлаждённые.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1950, 'Эмболия', 'Embolism', '', 2, 2, 4, 'Встакан со льдом налить гренадин, пряный ром и дополнить колой.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1952, 'Энергетическая Борзая', 'Energy Greyhound', '', 2, 2, 4, 'Украсить долькой свежего грейпфрута и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1957, 'Узколобый', 'Zipperhead', '', 2, 2, 4, 'В стакан вставит соломинку и положить лёд. Налить малиновый ликер, за тем водку и сверху содовую. Стараться не сильно перемешивать ингредиенты, чтобы до момента подачи коктейль был более - менее слоиствым.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1967, 'Умник', 'Egghead', '', 2, 2, 4, 'В шейкере со льдом встряхнуть водку, апельсиновый сок и сырое куриное яйцо. Процедить в стакан со льдом и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1970, 'Юрочка Гагарин', 'Yurochka Gagarin', '', 2, 4, 4, 'Сначала залпом выпить стакан водки, затем закурить папиросу "Беломорканал" и пока она курится выпить еще и стакан портвейна.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1995, 'Утро После', 'Morning After', '', 2, 2, 4, 'В шейкере со льдом встряхнуть абсент, яичный белок и анисовый ликер (или сироп), процедить в бокал, добавить содовую или лимонад.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1997, 'Уфимская Ночь', 'The Ufa Night', '', 2, 4, 4, 'В стакан налить водку и насыпать растворимый кофе, размешать  и долить колу.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (1998, 'Яблочный Спитцер', 'Apple Spritzer', '', 2, 2, 4, 'В стакан положить несколько кубиков льда и дольку яблока. Налить яблочный сок и дополнить содовой.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2002, 'Яблочный Штрудель', 'Apple Strudel', '', 2, 2, 4, 'В бокале со льдом смешать виски и яблочный сок. Добавить дольку лимона для украшения и подавть.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2003, 'Фак Ю!', 'Fuck You!', '', 2, 4, 4, 'В стакан наполовину заполненный льдом налить немного колы, затем виски и ягермастер. Потом можно дополнить стакан колой до краев.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2004, 'Явамастер', 'Javameister', '', 2, 2, 4, 'В чашку налить Ягермастер, за тем горячий кофе и сверху взбитые сливки.
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2025, 'Фиолетовые Небеса', 'Purple Heaven', '', 2, 2, 4, 'Налейте в стакан голубой Кюрасао, кокосовый ром (Малибу) и спрайт. Подкрасьте до желаемого оттенка гренадином и положите ягодку малины. Дождитесь, пока малинка опустится на дно, и подавайте.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2034, 'Поднять якоря!', 'Anchors Aweigh', '', 2, 2, 4, 'В шейкере со льдом встряхнуть бурбон, трипл сек, персиковый бренди, шерри бренди и сливки. Перелить в бокал со льдом и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2042, 'Ярость', 'Fury', '', 2, 4, 4, 'В стакан со льдом налить водку, виски, плеснуть кисло-сладкий микс и дополнить апельсиновым соком.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2043, 'Р - Флот', 'R Float', '', 2, 2, 4, 'В небольшой пивной бокал наполненный льдом налить все компоненты, перемешать и подавать.', null, 0, null, false, false, false, 0, 'highball');
INSERT INTO public.tbl_cocktails (id, name, name_en, description, complication_id, fortress_id, volume_id, recipe, mark, mark_cnt, preview_id, is_fire, is_flacky, is_iba, weight, icon) VALUES (2047, 'Фокси Леди', 'Foxy Lady', '', 2, 2, 4, 'Процедить в стакан со льдом и подавать.', null, 0, null, false, false, false, 0, 'highball');
